n class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;icon-rotate-180&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;&quot;&lt;/span&gt;&lt;span class=&quot;tag&quot; style=&quot;color:#1E347B;&quot;&gt;&amp;gt;&amp;lt;/i&amp;gt;&lt;/span&gt;&lt;/pre&gt;\n					&lt;/td&gt;\n				&lt;/tr&gt;\n				&lt;tr&gt;\n					&lt;td style=&quot;vertical-align:middle;&quot;&gt;\n						.icon-rotate-270\n					&lt;/td&gt;\n					&lt;td style=&quot;vertical-align:middle;&quot;&gt;\n						&lt;span class=&quot;icon icon-2x icon-flag icon-rotate-270&quot; style=&quot;font-family:ZenIcon;font-size:28px;line-height:1;&quot;&gt;&lt;/span&gt;\n					&lt;/td&gt;\n					&lt;td style=&quot;vertical-align:middle;&quot;&gt;\n&lt;pre class=&quot;prettyprint prettyprinted&quot;&gt;&lt;span class=&quot;tag&quot; style=&quot;color:#1E347B;&quot;&gt;&amp;lt;i&lt;/span&gt;&lt;span class=&quot;pln&quot; style=&quot;color:#48484C;&quot;&gt; &lt;/span&gt;&lt;span class=&quot;atn&quot; style=&quot;color:teal;&quot;&gt;class&lt;/span&gt;&lt;span class=&quot;pun&quot; style=&quot;color:#93A1A1;&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;&quot;icon icon-flag &lt;/span&gt;&lt;em&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;icon-rotate-270&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;&quot;&lt;/span&gt;&lt;span class=&quot;tag&quot; style=&quot;color:#1E347B;&quot;&gt;&amp;gt;&amp;lt;/i&amp;gt;&lt;/span&gt;&lt;/pre&gt;\n					&lt;/td&gt;\n				&lt;/tr&gt;\n				&lt;tr&gt;\n					&lt;td style=&quot;vertical-align:middle;&quot;&gt;\n						.icon-flip-horizontal\n					&lt;/td&gt;\n					&lt;td style=&quot;vertical-align:middle;&quot;&gt;\n						&lt;span class=&quot;icon icon-2x icon-flag icon-flip-horizontal&quot; style=&quot;font-family:ZenIcon;font-size:28px;line-height:1;&quot;&gt;&lt;/span&gt;\n					&lt;/td&gt;\n					&lt;td style=&quot;vertical-align:middle;&quot;&gt;\n&lt;pre class=&quot;prettyprint prettyprinted&quot;&gt;&lt;span class=&quot;tag&quot; style=&quot;color:#1E347B;&quot;&gt;&amp;lt;i&lt;/span&gt;&lt;span class=&quot;pln&quot; style=&quot;color:#48484C;&quot;&gt; &lt;/span&gt;&lt;span class=&quot;atn&quot; style=&quot;color:teal;&quot;&gt;class&lt;/span&gt;&lt;span class=&quot;pun&quot; style=&quot;color:#93A1A1;&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;&quot;icon icon-flag &lt;/span&gt;&lt;em&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;icon-flip-horizontal&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;&quot;&lt;/span&gt;&lt;span class=&quot;tag&quot; style=&quot;color:#1E347B;&quot;&gt;&amp;gt;&amp;lt;/i&amp;gt;&lt;/span&gt;&lt;/pre&gt;\n					&lt;/td&gt;\n				&lt;/tr&gt;\n				&lt;tr&gt;\n					&lt;td style=&quot;vertical-align:middle;&quot;&gt;\n						.icon-flip-vertical\n					&lt;/td&gt;\n					&lt;td style=&quot;vertical-align:middle;&quot;&gt;\n						&lt;span class=&quot;icon icon-2x icon-flag icon-flip-vertical&quot; style=&quot;font-family:ZenIcon;font-size:28px;line-height:1;&quot;&gt;&lt;/span&gt;\n					&lt;/td&gt;\n					&lt;td style=&quot;vertical-align:middle;&quot;&gt;\n&lt;pre class=&quot;prettyprint prettyprinted&quot;&gt;&lt;span class=&quot;tag&quot; style=&quot;color:#1E347B;&quot;&gt;&amp;lt;i&lt;/span&gt;&lt;span class=&quot;pln&quot; style=&quot;color:#48484C;&quot;&gt; &lt;/span&gt;&lt;span class=&quot;atn&quot; style=&quot;color:teal;&quot;&gt;class&lt;/span&gt;&lt;span class=&quot;pun&quot; style=&quot;color:#93A1A1;&quot;&gt;=&lt;/span&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;&quot;icon icon-flag &lt;/span&gt;&lt;em&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;icon-flip-vertical&lt;/span&gt;&lt;/em&gt;&lt;span class=&quot;atv&quot; style=&quot;color:#DD1144;&quot;&gt;&quot;&lt;/span&gt;&lt;span class=&quot;tag&quot